import React, { Fragment } from "react";
import { connect } from "react-redux";
import { 
  DatePicker, 
  Form, 
  Row, 
  Col, 
  Button,
  Icon,
  Drawer } from "antd";
import { getUserAppointments } from "../../actions/appointment/getuserappointments";
import { config } from "@fullcalendar/core";
import moment from "moment";
import {
  DATE_FORMAT,
  isMobile
} from "../../actions/types";

const { MonthPicker, RangePicker, WeekPicker } = DatePicker;

export class FilterUI extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isFilterDrawerVisible: false
    }
  }
  showDrawer = () => {
    this.setState({isFilterDrawerVisible: true})
  }

  onClose = () => {
    this.setState({isFilterDrawerVisible: false})
  }

  render() {
    return (
      <Fragment>
        { isMobile ? (<Fragment>
  <Button type="primary" onClick={this.showDrawer}>
  <Icon type="filter"/>Show Filters</Button>
  <Drawer onClose={this.onClose} visible={this.state.isFilterDrawerVisible}>
  {this.props.children}
  </Drawer>
</Fragment>) : this.props.children}
      </Fragment>
    )
}
}


class FilterAppointments extends React.Component {
  constructor(props) {
    super(props);
  }

  handleSubmit = e => {
      e.preventDefault();
      this.props.form.validateFields((err, values) => {
        if (!err) {
          this.props.getUserAppointments( 
              values.start_time.format(DATE_FORMAT),
              values.end_time.format(DATE_FORMAT));
        }
      });
    };

  render() {
    const { getFieldDecorator } = this.props.form;
    console.log(this.props.start_time);

    return (
      <Fragment>
        <Form onSubmit={this.handleSubmit} className="filterappoinments-form">
        <Row gutter={16} type="flex" align="middle">
            <Col xs={18} sm={12} md={6} lg={6}>
          <Form.Item label="Start Date" htmlFor="start_date">
            {getFieldDecorator("start_time", 
            {
              rules: [{required: false, message: 'Please select time!' }],
              initialValue: moment(this.props.start_date, DATE_FORMAT)
            })
            (<DatePicker />)}
          </Form.Item>
          </Col>
          <Col xs={18} sm={12} md={6} lg={6}>
          <Form.Item label="End Date" htmlFor="end_date">
            {getFieldDecorator("end_time", 
            {
              rules: [{required: false, message: 'Please select time!' }],
              initialValue: moment(this.props.end_date, DATE_FORMAT),
            })
            (<DatePicker />)}
          </Form.Item>
        </Col>
        <Col xs={18} sm={12} md={6} lg={6}>
        <Form.Item>
          <Button type="primary" htmlType="submit">
            Apply Filter
          </Button>
        </Form.Item>
        </Col>
        </Row>
        </Form>
      </Fragment>
    );
  }
}

const mapStateToProps = state => ({
  userAppointments: state.userAppointments
});
const FilterAppointmentsApp = connect(mapStateToProps, { getUserAppointments })(FilterAppointments)
const FilterAppointmentsForm = Form.create({ name: 'AppointmentsFilterForm' })(FilterAppointmentsApp);
//const FilterFormApp = connect(mapStateToProps, { })(FilterUI)
//const FilterForm = Form.create()(FilterUI);
export default FilterAppointmentsForm;
