import {
    SET_APPOINTMENT_FILTER,
    APPOINTMENTS_LOADING
} from "../../actions/types";
import {getUserAppointments} from "./getuserappointments"

export const filterAppointments =  (startDate='', endDate='', flag_team_appointments=false, userDetails=[]) => (dispatch, getState) => {
  return new Promise(function(resolve, reject) {
    resolve(dispatch({
      type: SET_APPOINTMENT_FILTER,
      payload: {
        startDate:startDate,
        endDate: endDate
      }
    }))
      .then( () => {
        dispatch(getUserAppointments(startDate, endDate, flag_team_appointments, ));
      })
      .catch(err => {
        console.log("GET err ", err);
        reject(err);
      });
  });
}
