import React, { Fragment } from "react";
import { connect } from "react-redux";
import { 
  DatePicker, 
  Form, 
  Input, 
  Row, 
  Col, 
  Button } from "antd";
import { getUserAppointments } from "../../actions/appointment/getuserappointments";

const { MonthPicker, RangePicker, WeekPicker } = DatePicker;

class FilterAppointments extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    const { getFieldDecorator } = this.props.form;

    onChange = e => {
      this.setState({ [e.target.name]: e.target.value });
    };

    handleSubmit = e => {
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
          if (!err) {
            this.props.getUserAppointments( 
                this.state.start_time,
                this.state.end_time);
          }
        });
      };

    return (
      <Fragment>
        <Form onSubmit={this.handleSubmit} className="filterappoinments-form">
        <Row gutter={16} type="flex">
            <Col xs={24} sm={16} md={12} lg={12}>
          <Form.Item label="Start Date" htmlFor="start_time">
            {getFieldDecorator("date-picker")(<DatePicker onChange={this.onChange}/>)}
          </Form.Item>
          </Col>
          <Col xs={24} sm={16} md={12} lg={12}>
          <Form.Item label="End Date" htmlFor="end_time">
            {getFieldDecorator("date-picker")(<DatePicker  onChange={this.onChange}/>)}
          </Form.Item>
        </Col>
        <Col xs={24} sm={16} md={12} lg={12}>
        <Form.Item>
          <Button type="primary" htmlType="submit">
            Apply Filter
          </Button>
        </Form.Item>
        </Col>
        </Row>
        </Form>
      </Fragment>
    );
  }
}


const mapStateToProps = state => ({
  userAppointments: state.userAppointments
});
export default connect(mapStateToProps, { getUserAppointments })(FilterAppointments)
