import {
    SET_APPOINTMENT_FILTER,
    APPOINTMENTS_LOADING
} from "../../actions/types";
import {getUserAppointments} from "./getuserappointments"

export const filterAppointments =  (startDate='', endDate='', flag_team_appointments=false) => (dispatch, getState) => {
  return new Promise((resolve, reject) => {
    dispatch({
      type: SET_APPOINTMENT_FILTER,
      payload: {
        startDate:startDate,
        endDate: endDate
      }
    })
      .then( () => {
        dispatch(getUserAppointments());
      })
      .catch(err => {
        console.log("GET err ", err);
        reject(err);
      });
  });
}
