import api from "../../apiurl";
import { tokenConfig } from "../accounts/usermanagement/auth";
import {
  APPOINTMENTS_FETCH_ERROR,
  APPOINTMENTS_LOADED,
  APPOINTMENTS_LOADING,
  DATE_FORMAT,
  YEAR_MONTH_FORMAT
} from "../types";
import moment from "moment";

export const getUserAppointments =  (start_time='', end_time='') => (
  dispatch,
  getState
) => {
  if (! start_time && ! end_time){
    var start_time = moment(new Date()).format(DATE_FORMAT);
    var end_time = moment().format("YYYY-MM-")  + moment().daysInMonth();
  }
  dispatch({ type: APPOINTMENTS_LOADING });
   api
    .get(
      "/api/v1/appointments/"+
      "?start_time_after="+start_time+"&end_time_before="+end_time,
      tokenConfig(getState)
    )
    .then(res => {
      dispatch({
        type: APPOINTMENTS_LOADED,
        payload: res.data.results
      });
    })
    .catch(err => {
      let err_msg = "";
      if ("detail" in err.response.data) {
        err_msg = err.response.data.detail;
      } else {
        for (var key in err.response.data) {
          err_msg += err.response.data[key];
        }
      }
      dispatch({
        type: APPOINTMENTS_FETCH_ERROR,
        payload: err_msg
      });
    });
};
