import React, { Fragment } from "react";
import { connect } from "react-redux";
import { DatePicker, Form, Row, Col, Button, Icon, Drawer } from "antd";
import { getUserAppointments } from "../../actions/appointment/getuserappointments";
import { config } from "@fullcalendar/core";
import moment from "moment";
import { DATE_FORMAT, isMobile } from "../../actions/types";

const { MonthPicker, RangePicker, WeekPicker } = DatePicker;

export class FilterUI extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isFilterDrawerVisible: false
    };
  }
  showDrawer = () => {
    this.setState({ isFilterDrawerVisible: true });
  };

  onClose = () => {
    this.setState({ isFilterDrawerVisible: false });
  };

  render() {
    return (
      <Fragment>
        {isMobile ? (
          <Fragment >
            <Button type="primary" onClick={this.showDrawer} style={{ marginBottom: '10px'}}>
              <Icon type="filter" />
              Show Filters
            </Button>
            <Drawer
              onClose={this.onClose}
              visible={this.state.isFilterDrawerVisible}>
              {this.props.children}
            </Drawer>
          </Fragment>
        ) : (
          this.props.children
        )}
      </Fragment>
    );
  }
}

class FilterAppointments extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      start_date:moment(this.props.start_date).format(DATE_FORMAT) ,
      end_date: moment(this.props.end_date).format(DATE_FORMAT)
    }
  }

  setStartDate = (value) => {
    this.setState({ start_date: value.format(DATE_FORMAT) },this.handleFilter);
  }
  setEndDate = (value) => {
    this.setState({ end_date: value.format(DATE_FORMAT)},this.handleFilter);
  }
  handleFilter = () => {
    this.props.getUserAppointments(
      this.state.start_date,
      this.state.end_date 
    );
  }

  // handleSubmit = e => {
  //   // e.preventDefault();
  //   this.props.form.validateFields((err, values) => {
  //     if (!err) {
  //       this.props.getUserAppointments(
  //         this.state.start_date.format(DATE_FORMAT),
  //         this.state.end_date.format(DATE_FORMAT)
  //       );
  //     }
  //   });
  // };

  render() {
    const { getFieldDecorator } = this.props.form;
    const formItemLayout = {
      labelCol: { span: 8 },
      wrapperCol: { span: 16 }
    };

    return (
      <Fragment>
        <Form layout="inline" className="filterappoinments-form" style={{marginBottom: '15px'}}>
          <Row gutter={16} type="flex">
          <Col xs={10} sm={10} md={14} lg={14}/>
            <Col xs={7} sm={7} md={5} lg={5}>
              <Form.Item  {...formItemLayout} label="From" htmlFor="start_date">
                {getFieldDecorator("start_time", {
                  rules: [{ required: false, message: "Please select time!" }],
                  initialValue: moment(this.state.start_date, DATE_FORMAT)
                })(<DatePicker onChange={this.setStartDate} />)}
              </Form.Item>
            </Col>
            <Col xs={7} sm={7} md={6} lg={5}>
              <Form.Item  {...formItemLayout} label="To" htmlFor="end_date">
                {getFieldDecorator("end_time", {
                  rules: [{ required: false, message: "Please select time!" }],
                  initialValue: moment(this.props.end_date, DATE_FORMAT)
                })(<DatePicker  onChange={(value)=>this.setEndDate(value)} />)}
              </Form.Item>
            </Col>
            </Row>
        </Form>
      </Fragment>
    );
  }
}

const mapStateToProps = state => ({
  userAppointments: state.userAppointments
});
const FilterAppointmentsApp = connect(
  mapStateToProps,
  { getUserAppointments }
)(FilterAppointments);
const FilterAppointmentsForm = Form.create({ name: "AppointmentsFilterForm" })(
  FilterAppointmentsApp
);
//const FilterFormApp = connect(mapStateToProps, { })(FilterUI)
//const FilterForm = Form.create()(FilterUI);
export default FilterAppointmentsForm;
